mapper.select:
		select (@i:=@i+1) rowNum,
			f_tinyint as fTinyint,
			f_smallint as fSmallint,
			f_mediumint as fMediumint,
			f_int as fInt,
			f_integer as fInteger,
			f_bigint as fBigint,
			f_double as fDouble,
			f_float as fFloat,
			f_decimal as fDecimal,
			f_numeric as fNumeric,
			f_char as fChar,
			f_varchar as fVarchar,
			f_year as fYear,
			f_tinytext as fTinytext,
			f_text as fText,
			f_longtext as fLongtext,
			isDet as isDet
		 from a, (SELECT @i:=0) as i

            and f_tinyint=#{fTinyint}
            and f_smallint=#{fSmallint}
            and f_mediumint=#{fMediumint}
            and f_int=#{fInt}
            and f_integer=#{fInteger}
            and f_bigint=#{fBigint}
            and f_double=#{fDouble}
            and f_float=#{fFloat}
            and f_decimal=#{fDecimal}
            and f_numeric=#{fNumeric}
            and f_char=#{fChar}
            and f_varchar=#{fVarchar}
            and f_year=#{fYear}
            and f_tinytext=#{fTinytext}
            and f_text=#{fText}
            and f_longtext=#{fLongtext}
            and isDet=#{isDet}
            ${sqlWhere}

        order by ${sqlOrderBy}
        LIMIT #{sqlStartIndex},#{sqlPageSize}

mapper.update:
update a
f_smallint=null, f_smallint=#{fSmallint}, f_mediumint=null, f_mediumint=#{fMediumint}, f_int=null, f_int=#{fInt}, f_integer=null, f_integer=#{fInteger}, f_bigint=null, f_bigint=#{fBigint}, f_double=null, f_double=#{fDouble}, f_float=null, f_float=#{fFloat}, f_decimal=null, f_decimal=#{fDecimal}, f_numeric=null, f_numeric=#{fNumeric}, f_char=null, f_char=#{fChar}, f_varchar=null, f_varchar=#{fVarchar}, f_year=null, f_year=#{fYear}, f_tinytext=null, f_tinytext=#{fTinytext}, f_text=null, f_text=#{fText}, f_longtext=null, f_longtext=#{fLongtext}, isDet=null, isDet=#{isDet},
${sqlWhere}

update a
f_smallint=null, f_smallint=#{fSmallint}, f_mediumint=null, f_mediumint=#{fMediumint}, f_int=null, f_int=#{fInt}, f_integer=null, f_integer=#{fInteger}, f_bigint=null, f_bigint=#{fBigint}, f_double=null, f_double=#{fDouble}, f_float=null, f_float=#{fFloat}, f_decimal=null, f_decimal=#{fDecimal}, f_numeric=null, f_numeric=#{fNumeric}, f_char=null, f_char=#{fChar}, f_varchar=null, f_varchar=#{fVarchar}, f_year=null, f_year=#{fYear}, f_tinytext=null, f_tinytext=#{fTinytext}, f_text=null, f_text=#{fText}, f_longtext=null, f_longtext=#{fLongtext}, isDet=null, isDet=#{isDet},
where f_tinyint=#{_parameter}
