mapper.select:
		select (@i:=@i+1) rn,
			id as id,
			roleName as roleName,
			note as note,
			price3 as price3
		 from gys, (SELECT @i:=0) as i


                and id like concat('%',#{id},'%')
                and id like concat(#{id},'%')
