mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select en_name as enName,expressfunction as expressfunction,site_flag as siteFlag,org_code as orgCode,post_code as postCode,county_code as countyCode,range as range,custom_office_code as customOfficeCode,custom_station_flag as customStationFlag,city_code as cityCode,exch_bureau_code as exchBureauCode,exch_bureau_flag as exchBureauFlag,org_full_name as orgFullName,prov_code as provCode,site_level as siteLevel,org_abbr_spel as orgAbbrSpel,up_org_code as upOrgCode,expresslevel as expresslevel,cltlevel as cltlevel,org_level_code as orgLevelCode,org_abbr_name as orgAbbrName from tb_res_org_m

            and en_name=#{enName}
            and expressfunction=#{expressfunction}
            and site_flag=#{siteFlag}
            and org_code=#{orgCode}
            and post_code=#{postCode}
            and county_code=#{countyCode}
            and range=#{range}
            and custom_office_code=#{customOfficeCode}
            and custom_station_flag=#{customStationFlag}
            and city_code=#{cityCode}
            and exch_bureau_code=#{exchBureauCode}
            and exch_bureau_flag=#{exchBureauFlag}
            and org_full_name=#{orgFullName}
            and prov_code=#{provCode}
            and site_level=#{siteLevel}
            and org_abbr_spel=#{orgAbbrSpel}
            and up_org_code=#{upOrgCode}
            and expresslevel=#{expresslevel}
            and cltlevel=#{cltlevel}
            and org_level_code=#{orgLevelCode}
            and org_abbr_name=#{orgAbbrName}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update tb_res_org_m
en_name=null, en_name=#{enName}, expressfunction=null, expressfunction=#{expressfunction}, site_flag=null, site_flag=#{siteFlag}, org_code=null, org_code=#{orgCode}, post_code=null, post_code=#{postCode}, county_code=null, county_code=#{countyCode}, range=null, range=#{range}, custom_office_code=null, custom_office_code=#{customOfficeCode}, custom_station_flag=null, custom_station_flag=#{customStationFlag}, city_code=null, city_code=#{cityCode}, exch_bureau_code=null, exch_bureau_code=#{exchBureauCode}, exch_bureau_flag=null, exch_bureau_flag=#{exchBureauFlag}, org_full_name=null, org_full_name=#{orgFullName}, prov_code=null, prov_code=#{provCode}, site_level=null, site_level=#{siteLevel}, org_abbr_spel=null, org_abbr_spel=#{orgAbbrSpel}, up_org_code=null, up_org_code=#{upOrgCode}, expresslevel=null, expresslevel=#{expresslevel}, cltlevel=null, cltlevel=#{cltlevel}, org_level_code=null, org_level_code=#{orgLevelCode}, org_abbr_name=null, org_abbr_name=#{orgAbbrName},
${sqlWhere}
