mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select org_name_ls as orgNameLs,org_code_ls as orgCodeLs,sort_index as sortIndex,org_level as orgLevel,super_org_code as superOrgCode from td_s_org_ls

            and org_name_ls=#{orgNameLs}
            and org_code_ls=#{orgCodeLs}
            and sort_index=#{sortIndex}
            and org_level=#{orgLevel}
            and super_org_code=#{superOrgCode}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_s_org_ls
org_name_ls=null, org_name_ls=#{orgNameLs}, org_code_ls=null, org_code_ls=#{orgCodeLs}, sort_index=null, sort_index=#{sortIndex}, org_level=null, org_level=#{orgLevel}, super_org_code=null, super_org_code=#{superOrgCode},
${sqlWhere}
