mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select remark as remark,create_date as createDate,edit_id as editId,sid as sid,create_id as createId,admin_role_id as adminRoleId,role_id as roleId,edit_date as editDate from td_sys_admin_level

            and remark=#{remark}
            and create_date=#{createDate}
            and edit_id=#{editId}
            and sid=#{sid}
            and create_id=#{createId}
            and admin_role_id=#{adminRoleId}
            and role_id=#{roleId}
            and edit_date=#{editDate}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_admin_level
remark=null, remark=#{remark}, create_date=null, create_date=#{createDate}, edit_id=null, edit_id=#{editId}, sid=null, sid=#{sid}, create_id=null, create_id=#{createId}, admin_role_id=null, admin_role_id=#{adminRoleId}, role_id=null, role_id=#{roleId}, edit_date=null, edit_date=#{editDate},
${sqlWhere}
