mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,create_date as createDate,board_name as boardName,online_time as onlineTime,edit_date as editDate,board_type as boardType,remark as remark,board_url as boardUrl,edit_id as editId,create_id as createId,board_code as boardCode,board_icon as boardIcon,board_desc as boardDesc,org_level_code as orgLevelCode from td_sys_board

            and sid=#{sid}
            and create_date=#{createDate}
            and board_name=#{boardName}
            and online_time=#{onlineTime}
            and edit_date=#{editDate}
            and board_type=#{boardType}
            and remark=#{remark}
            and board_url=#{boardUrl}
            and edit_id=#{editId}
            and create_id=#{createId}
            and board_code=#{boardCode}
            and board_icon=#{boardIcon}
            and board_desc=#{boardDesc}
            and org_level_code=#{orgLevelCode}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_board
create_date=null, create_date=#{createDate}, board_name=null, board_name=#{boardName}, online_time=null, online_time=#{onlineTime}, edit_date=null, edit_date=#{editDate}, board_type=null, board_type=#{boardType}, remark=null, remark=#{remark}, board_url=null, board_url=#{boardUrl}, edit_id=null, edit_id=#{editId}, create_id=null, create_id=#{createId}, board_code=null, board_code=#{boardCode}, board_icon=null, board_icon=#{boardIcon}, board_desc=null, board_desc=#{boardDesc}, org_level_code=null, org_level_code=#{orgLevelCode},
${sqlWhere}

update td_sys_board
create_date=null, create_date=#{createDate}, board_name=null, board_name=#{boardName}, online_time=null, online_time=#{onlineTime}, edit_date=null, edit_date=#{editDate}, board_type=null, board_type=#{boardType}, remark=null, remark=#{remark}, board_url=null, board_url=#{boardUrl}, edit_id=null, edit_id=#{editId}, create_id=null, create_id=#{createId}, board_code=null, board_code=#{boardCode}, board_icon=null, board_icon=#{boardIcon}, board_desc=null, board_desc=#{boardDesc}, org_level_code=null, org_level_code=#{orgLevelCode},
where sid=#{_parameter}
