mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,create_date as createDate,dict_desc as dictDesc,edit_id as editId,dict_type_name as dictTypeName,dict_value as dictValue,create_id as createId,dict_name as dictName,dict_id as dictId,edit_date as editDate,sort as sort from td_sys_directory

            and sid=#{sid}
            and create_date=#{createDate}
            and dict_desc=#{dictDesc}
            and edit_id=#{editId}
            and dict_type_name=#{dictTypeName}
            and dict_value=#{dictValue}
            and create_id=#{createId}
            and dict_name=#{dictName}
            and dict_id=#{dictId}
            and edit_date=#{editDate}
            and sort=#{sort}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_directory
create_date=null, create_date=#{createDate}, dict_desc=null, dict_desc=#{dictDesc}, edit_id=null, edit_id=#{editId}, dict_type_name=null, dict_type_name=#{dictTypeName}, dict_value=null, dict_value=#{dictValue}, create_id=null, create_id=#{createId}, dict_name=null, dict_name=#{dictName}, dict_id=null, dict_id=#{dictId}, edit_date=null, edit_date=#{editDate}, sort=null, sort=#{sort},
${sqlWhere}

update td_sys_directory
create_date=null, create_date=#{createDate}, dict_desc=null, dict_desc=#{dictDesc}, edit_id=null, edit_id=#{editId}, dict_type_name=null, dict_type_name=#{dictTypeName}, dict_value=null, dict_value=#{dictValue}, create_id=null, create_id=#{createId}, dict_name=null, dict_name=#{dictName}, dict_id=null, dict_id=#{dictId}, edit_date=null, edit_date=#{editDate}, sort=null, sort=#{sort},
where sid=#{_parameter}
