mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,dict_type_code as dictTypeCode,create_date as createDate,dict_desc as dictDesc,edit_id as editId,dict_type_name as dictTypeName,create_id as createId,edit_date as editDate from td_sys_directory_type

            and sid=#{sid}
            and dict_type_code=#{dictTypeCode}
            and create_date=#{createDate}
            and dict_desc=#{dictDesc}
            and edit_id=#{editId}
            and dict_type_name=#{dictTypeName}
            and create_id=#{createId}
            and edit_date=#{editDate}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_directory_type
dict_type_code=null, dict_type_code=#{dictTypeCode}, create_date=null, create_date=#{createDate}, dict_desc=null, dict_desc=#{dictDesc}, edit_id=null, edit_id=#{editId}, dict_type_name=null, dict_type_name=#{dictTypeName}, create_id=null, create_id=#{createId}, edit_date=null, edit_date=#{editDate},
${sqlWhere}

update td_sys_directory_type
dict_type_code=null, dict_type_code=#{dictTypeCode}, create_date=null, create_date=#{createDate}, dict_desc=null, dict_desc=#{dictDesc}, edit_id=null, edit_id=#{editId}, dict_type_name=null, dict_type_name=#{dictTypeName}, create_id=null, create_id=#{createId}, edit_date=null, edit_date=#{editDate},
where sid=#{_parameter}
