mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,func_desc as funcDesc,remark as remark,create_date as createDate,edit_id as editId,target as target,func_code as funcCode,create_id as createId,edit_date as editDate,func_type as funcType,func_url as funcUrl,func_name as funcName from td_sys_func

            and sid=#{sid}
            and func_desc=#{funcDesc}
            and remark=#{remark}
            and create_date=#{createDate}
            and edit_id=#{editId}
            and target=#{target}
            and func_code=#{funcCode}
            and create_id=#{createId}
            and edit_date=#{editDate}
            and func_type=#{funcType}
            and func_url=#{funcUrl}
            and func_name=#{funcName}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_func
func_desc=null, func_desc=#{funcDesc}, remark=null, remark=#{remark}, create_date=null, create_date=#{createDate}, edit_id=null, edit_id=#{editId}, target=null, target=#{target}, func_code=null, func_code=#{funcCode}, create_id=null, create_id=#{createId}, edit_date=null, edit_date=#{editDate}, func_type=null, func_type=#{funcType}, func_url=null, func_url=#{funcUrl}, func_name=null, func_name=#{funcName},
${sqlWhere}

update td_sys_func
func_desc=null, func_desc=#{funcDesc}, remark=null, remark=#{remark}, create_date=null, create_date=#{createDate}, edit_id=null, edit_id=#{editId}, target=null, target=#{target}, func_code=null, func_code=#{funcCode}, create_id=null, create_id=#{createId}, edit_date=null, edit_date=#{editDate}, func_type=null, func_type=#{funcType}, func_url=null, func_url=#{funcUrl}, func_name=null, func_name=#{funcName},
where sid=#{_parameter}
