mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select user_systemos as userSystemos,sid as sid,session_id as sessionId,operate_type as operateType,http_params as httpParams,ip_address as ipAddress,http_type as httpType,func_url as funcUrl,remark as remark,operate_result as operateResult,operate_date as operateDate,mac_address as macAddress,user_brower as userBrower,user_id as userId,func_id as funcId from td_sys_log_operate

            and user_systemos=#{userSystemos}
            and sid=#{sid}
            and session_id=#{sessionId}
            and operate_type=#{operateType}
            and http_params=#{httpParams}
            and ip_address=#{ipAddress}
            and http_type=#{httpType}
            and func_url=#{funcUrl}
            and remark=#{remark}
            and operate_result=#{operateResult}
            and operate_date=#{operateDate}
            and mac_address=#{macAddress}
            and user_brower=#{userBrower}
            and user_id=#{userId}
            and func_id=#{funcId}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_log_operate
user_systemos=null, user_systemos=#{userSystemos}, sid=null, sid=#{sid}, session_id=null, session_id=#{sessionId}, operate_type=null, operate_type=#{operateType}, http_params=null, http_params=#{httpParams}, ip_address=null, ip_address=#{ipAddress}, http_type=null, http_type=#{httpType}, func_url=null, func_url=#{funcUrl}, remark=null, remark=#{remark}, operate_result=null, operate_result=#{operateResult}, operate_date=null, operate_date=#{operateDate}, mac_address=null, mac_address=#{macAddress}, user_brower=null, user_brower=#{userBrower}, user_id=null, user_id=#{userId}, func_id=null, func_id=#{funcId},
${sqlWhere}
