mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select remark as remark,create_date as createDate,up_org_level_id as upOrgLevelId,edit_id as editId,sid as sid,org_level_name as orgLevelName,create_id as createId,edit_date as editDate,org_level_code as orgLevelCode,admin_scope as adminScope from td_sys_org_level

            and remark=#{remark}
            and create_date=#{createDate}
            and up_org_level_id=#{upOrgLevelId}
            and edit_id=#{editId}
            and sid=#{sid}
            and org_level_name=#{orgLevelName}
            and create_id=#{createId}
            and edit_date=#{editDate}
            and org_level_code=#{orgLevelCode}
            and admin_scope=#{adminScope}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_org_level
remark=null, remark=#{remark}, create_date=null, create_date=#{createDate}, up_org_level_id=null, up_org_level_id=#{upOrgLevelId}, edit_id=null, edit_id=#{editId}, sid=null, sid=#{sid}, org_level_name=null, org_level_name=#{orgLevelName}, create_id=null, create_id=#{createId}, edit_date=null, edit_date=#{editDate}, org_level_code=null, org_level_code=#{orgLevelCode}, admin_scope=null, admin_scope=#{adminScope},
${sqlWhere}
