mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select org_name as orgName,create_date as createDate,parent_org_code as parentOrgCode,sid as sid,post_code as postCode,county_code as countyCode,edit_date as editDate,city_code as cityCode,prov_code as provCode,edit_id as editId,order_index as orderIndex,create_id as createId,come_from as comeFrom,org_short_name as orgShortName,status as status,org_level_code as orgLevelCode from td_sys_org

            and org_name=#{orgName}
            and create_date=#{createDate}
            and parent_org_code=#{parentOrgCode}
            and sid=#{sid}
            and post_code=#{postCode}
            and county_code=#{countyCode}
            and edit_date=#{editDate}
            and city_code=#{cityCode}
            and prov_code=#{provCode}
            and edit_id=#{editId}
            and order_index=#{orderIndex}
            and create_id=#{createId}
            and come_from=#{comeFrom}
            and org_short_name=#{orgShortName}
            and status=#{status}
            and org_level_code=#{orgLevelCode}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_org
org_name=null, org_name=#{orgName}, create_date=null, create_date=#{createDate}, parent_org_code=null, parent_org_code=#{parentOrgCode}, sid=null, sid=#{sid}, post_code=null, post_code=#{postCode}, county_code=null, county_code=#{countyCode}, edit_date=null, edit_date=#{editDate}, city_code=null, city_code=#{cityCode}, prov_code=null, prov_code=#{provCode}, edit_id=null, edit_id=#{editId}, order_index=null, order_index=#{orderIndex}, create_id=null, create_id=#{createId}, come_from=null, come_from=#{comeFrom}, org_short_name=null, org_short_name=#{orgShortName}, status=null, status=#{status}, org_level_code=null, org_level_code=#{orgLevelCode},
${sqlWhere}
