mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,remark as remark,create_date as createDate,board_id as boardId,create_id as createId,sort_index as sortIndex,role_id as roleId from td_sys_role_board_rs

            and sid=#{sid}
            and remark=#{remark}
            and create_date=#{createDate}
            and board_id=#{boardId}
            and create_id=#{createId}
            and sort_index=#{sortIndex}
            and role_id=#{roleId}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_role_board_rs
remark=null, remark=#{remark}, create_date=null, create_date=#{createDate}, board_id=null, board_id=#{boardId}, create_id=null, create_id=#{createId}, sort_index=null, sort_index=#{sortIndex}, role_id=null, role_id=#{roleId},
${sqlWhere}

update td_sys_role_board_rs
remark=null, remark=#{remark}, create_date=null, create_date=#{createDate}, board_id=null, board_id=#{boardId}, create_id=null, create_id=#{createId}, sort_index=null, sort_index=#{sortIndex}, role_id=null, role_id=#{roleId},
where sid=#{_parameter}
