mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,create_date as createDate,role_status as roleStatus,parent_id as parentId,role_type as roleType,role_code as roleCode,edit_date as editDate,role_name as roleName,dept_id as deptId,remark as remark,edit_id as editId,create_id as createId,is_show as isShow,org_level_code as orgLevelCode from td_sys_role

            and sid=#{sid}
            and create_date=#{createDate}
            and role_status=#{roleStatus}
            and parent_id=#{parentId}
            and role_type=#{roleType}
            and role_code=#{roleCode}
            and edit_date=#{editDate}
            and role_name=#{roleName}
            and dept_id=#{deptId}
            and remark=#{remark}
            and edit_id=#{editId}
            and create_id=#{createId}
            and is_show=#{isShow}
            and org_level_code=#{orgLevelCode}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_role
create_date=null, create_date=#{createDate}, role_status=null, role_status=#{roleStatus}, parent_id=null, parent_id=#{parentId}, role_type=null, role_type=#{roleType}, role_code=null, role_code=#{roleCode}, edit_date=null, edit_date=#{editDate}, role_name=null, role_name=#{roleName}, dept_id=null, dept_id=#{deptId}, remark=null, remark=#{remark}, edit_id=null, edit_id=#{editId}, create_id=null, create_id=#{createId}, is_show=null, is_show=#{isShow}, org_level_code=null, org_level_code=#{orgLevelCode},
${sqlWhere}

update td_sys_role
create_date=null, create_date=#{createDate}, role_status=null, role_status=#{roleStatus}, parent_id=null, parent_id=#{parentId}, role_type=null, role_type=#{roleType}, role_code=null, role_code=#{roleCode}, edit_date=null, edit_date=#{editDate}, role_name=null, role_name=#{roleName}, dept_id=null, dept_id=#{deptId}, remark=null, remark=#{remark}, edit_id=null, edit_id=#{editId}, create_id=null, create_id=#{createId}, is_show=null, is_show=#{isShow}, org_level_code=null, org_level_code=#{orgLevelCode},
where sid=#{_parameter}
