mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,user_ext_name as userExtName,create_date as createDate,from_columns as fromColumns,from_value_column as fromValueColumn,user_ext_alias as userExtAlias,edit_date as editDate,user_ext_type as userExtType,from_table as fromTable,from_table_sql as fromTableSql,remark as remark,user_ext_code as userExtCode,edit_id as editId,create_id as createId,from_key_column as fromKeyColumn from td_sys_user_ext_config

            and sid=#{sid}
            and user_ext_name=#{userExtName}
            and create_date=#{createDate}
            and from_columns=#{fromColumns}
            and from_value_column=#{fromValueColumn}
            and user_ext_alias=#{userExtAlias}
            and edit_date=#{editDate}
            and user_ext_type=#{userExtType}
            and from_table=#{fromTable}
            and from_table_sql=#{fromTableSql}
            and remark=#{remark}
            and user_ext_code=#{userExtCode}
            and edit_id=#{editId}
            and create_id=#{createId}
            and from_key_column=#{fromKeyColumn}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

