mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,remark as remark,user_ext_id as userExtId,create_date as createDate,create_id as createId,user_id as userId,user_ext_value as userExtValue from td_sys_user_ext

            and sid=#{sid}
            and remark=#{remark}
            and user_ext_id=#{userExtId}
            and create_date=#{createDate}
            and create_id=#{createId}
            and user_id=#{userId}
            and user_ext_value=#{userExtValue}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_user_ext
remark=null, remark=#{remark}, user_ext_id=null, user_ext_id=#{userExtId}, create_date=null, create_date=#{createDate}, create_id=null, create_id=#{createId}, user_id=null, user_id=#{userId}, user_ext_value=null, user_ext_value=#{userExtValue},
${sqlWhere}

update td_sys_user_ext
remark=null, remark=#{remark}, user_ext_id=null, user_ext_id=#{userExtId}, create_date=null, create_date=#{createDate}, create_id=null, create_id=#{createId}, user_id=null, user_id=#{userId}, user_ext_value=null, user_ext_value=#{userExtValue},
where sid=#{_parameter}
