mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,create_date as createDate,position as position,is_super_admin as isSuperAdmin,rember_pass as remberPass,edit_date as editDate,user_name as userName,department as department,email as email,remark as remark,edit_id as editId,user_code as userCode,create_id as createId,phone as phone,password as password,status as status,org_level_code as orgLevelCode from td_sys_user

            and sid=#{sid}
            and create_date=#{createDate}
            and position=#{position}
            and is_super_admin=#{isSuperAdmin}
            and rember_pass=#{remberPass}
            and edit_date=#{editDate}
            and user_name=#{userName}
            and department=#{department}
            and email=#{email}
            and remark=#{remark}
            and edit_id=#{editId}
            and user_code=#{userCode}
            and create_id=#{createId}
            and phone=#{phone}
            and password=#{password}
            and status=#{status}
            and org_level_code=#{orgLevelCode}
            and ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_user
create_date=null, create_date=#{createDate}, position=null, position=#{position}, is_super_admin=null, is_super_admin=#{isSuperAdmin}, rember_pass=null, rember_pass=#{remberPass}, edit_date=null, edit_date=#{editDate}, user_name=null, user_name=#{userName}, department=null, department=#{department}, email=null, email=#{email}, remark=null, remark=#{remark}, edit_id=null, edit_id=#{editId}, user_code=null, user_code=#{userCode}, create_id=null, create_id=#{createId}, phone=null, phone=#{phone}, password=null, password=#{password}, status=null, status=#{status}, org_level_code=null, org_level_code=#{orgLevelCode},
${sqlWhere}

update td_sys_user
create_date=null, create_date=#{createDate}, position=null, position=#{position}, is_super_admin=null, is_super_admin=#{isSuperAdmin}, rember_pass=null, rember_pass=#{remberPass}, edit_date=null, edit_date=#{editDate}, user_name=null, user_name=#{userName}, department=null, department=#{department}, email=null, email=#{email}, remark=null, remark=#{remark}, edit_id=null, edit_id=#{editId}, user_code=null, user_code=#{userCode}, create_id=null, create_id=#{createId}, phone=null, phone=#{phone}, password=null, password=#{password}, status=null, status=#{status}, org_level_code=null, org_level_code=#{orgLevelCode},
where sid=#{sid}
