mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select sid as sid,create_date as createDate,edit_id as editId,remaik as remaik,create_id as createId,sort_index as sortIndex,role_id as roleId,edit_date as editDate,user_name as userName,user_id as userId,role_name as roleName from td_sys_user_role_rs

            and sid=#{sid}
            and create_date=#{createDate}
            and edit_id=#{editId}
            and remaik=#{remaik}
            and create_id=#{createId}
            and sort_index=#{sortIndex}
            and role_id=#{roleId}
            and edit_date=#{editDate}
            and user_name=#{userName}
            and user_id=#{userId}
            and role_name=#{roleName}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_sys_user_role_rs
create_date=null, create_date=#{createDate}, edit_id=null, edit_id=#{editId}, remaik=null, remaik=#{remaik}, create_id=null, create_id=#{createId}, sort_index=null, sort_index=#{sortIndex}, role_id=null, role_id=#{roleId}, edit_date=null, edit_date=#{editDate}, user_name=null, user_name=#{userName}, user_id=null, user_id=#{userId}, role_name=null, role_name=#{roleName},
${sqlWhere}

update td_sys_user_role_rs
create_date=null, create_date=#{createDate}, edit_id=null, edit_id=#{editId}, remaik=null, remaik=#{remaik}, create_id=null, create_id=#{createId}, sort_index=null, sort_index=#{sortIndex}, role_id=null, role_id=#{roleId}, edit_date=null, edit_date=#{editDate}, user_name=null, user_name=#{userName}, user_id=null, user_id=#{userId}, role_name=null, role_name=#{roleName},
where sid=#{_parameter}
