mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select create_user as createUser,create_time as createTime,board_name as boardName,board_id as boardId from td_tvcm_board_info

            and create_user=#{createUser}
            and create_time=#{createTime}
            and board_name=#{boardName}
            and board_id=#{boardId}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_board_info
create_user=null, create_user=#{createUser}, create_time=null, create_time=#{createTime}, board_name=null, board_name=#{boardName}, board_id=null, board_id=#{boardId},
${sqlWhere}
