mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select create_time as createTime,table_name as tableName,col_name as colName,table_desc as tableDesc,dim_id as dimId,dim_name as dimName,create_user as createUser,col_code as colCode from td_tvcm_dimention_info

            and create_time=#{createTime}
            and table_name=#{tableName}
            and col_name=#{colName}
            and table_desc=#{tableDesc}
            and dim_id=#{dimId}
            and dim_name=#{dimName}
            and create_user=#{createUser}
            and col_code=#{colCode}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_dimention_info
create_time=null, create_time=#{createTime}, table_name=null, table_name=#{tableName}, col_name=null, col_name=#{colName}, table_desc=null, table_desc=#{tableDesc}, dim_id=null, dim_id=#{dimId}, dim_name=null, dim_name=#{dimName}, create_user=null, create_user=#{createUser}, col_code=null, col_code=#{colCode},
${sqlWhere}
