mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select idx_id as idxId,create_date as createDate,idx_tepl_id as idxTeplId,data_table as dataTable,update_flag as updateFlag,valid_flag as validFlag,page_id as pageId,create_user as createUser,page_name as pageName,tepl_id as teplId,idx_name as idxName from td_tvcm_idx_tepl_rs

            and idx_id=#{idxId}
            and create_date=#{createDate}
            and idx_tepl_id=#{idxTeplId}
            and data_table=#{dataTable}
            and update_flag=#{updateFlag}
            and valid_flag=#{validFlag}
            and page_id=#{pageId}
            and create_user=#{createUser}
            and page_name=#{pageName}
            and tepl_id=#{teplId}
            and idx_name=#{idxName}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_idx_tepl_rs
idx_id=null, idx_id=#{idxId}, create_date=null, create_date=#{createDate}, idx_tepl_id=null, idx_tepl_id=#{idxTeplId}, data_table=null, data_table=#{dataTable}, update_flag=null, update_flag=#{updateFlag}, valid_flag=null, valid_flag=#{validFlag}, page_id=null, page_id=#{pageId}, create_user=null, create_user=#{createUser}, page_name=null, page_name=#{pageName}, tepl_id=null, tepl_id=#{teplId}, idx_name=null, idx_name=#{idxName},
${sqlWhere}
