mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select idx_id as idxId,create_time as createTime,idx_desc as idxDesc,board_id as boardId,create_user as createUser,org_lvl_name as orgLvlName,target_date as targetDate,org_lvl_code as orgLvlCode,idx_name as idxName from td_tvcm_index_info

            and idx_id=#{idxId}
            and create_time=#{createTime}
            and idx_desc=#{idxDesc}
            and board_id=#{boardId}
            and create_user=#{createUser}
            and org_lvl_name=#{orgLvlName}
            and target_date=#{targetDate}
            and org_lvl_code=#{orgLvlCode}
            and idx_name=#{idxName}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_index_info
idx_id=null, idx_id=#{idxId}, create_time=null, create_time=#{createTime}, idx_desc=null, idx_desc=#{idxDesc}, board_id=null, board_id=#{boardId}, create_user=null, create_user=#{createUser}, org_lvl_name=null, org_lvl_name=#{orgLvlName}, target_date=null, target_date=#{targetDate}, org_lvl_code=null, org_lvl_code=#{orgLvlCode}, idx_name=null, idx_name=#{idxName},
${sqlWhere}
