mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select org_name as orgName,org_code as orgCode,page_id as pageId,month as month,page_name as pageName,value as value,year as year from td_tvcm_it0001

            and org_name=#{orgName}
            and org_code=#{orgCode}
            and page_id=#{pageId}
            and month=#{month}
            and page_name=#{pageName}
            and value=#{value}
            and year=#{year}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_it0001
org_name=null, org_name=#{orgName}, org_code=null, org_code=#{orgCode}, page_id=null, page_id=#{pageId}, month=null, month=#{month}, page_name=null, page_name=#{pageName}, value=null, value=#{value}, year=null, year=#{year},
${sqlWhere}
