mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select create_user as createUser,create_date as createDate,user_id as userId,idx_dim_id as idxDimId from td_tvcm_target_auth_cfg

            and create_user=#{createUser}
            and create_date=#{createDate}
            and user_id=#{userId}
            and idx_dim_id=#{idxDimId}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_target_auth_cfg
create_user=null, create_user=#{createUser}, create_date=null, create_date=#{createDate}, user_id=null, user_id=#{userId}, idx_dim_id=null, idx_dim_id=#{idxDimId},
${sqlWhere}
