mapper.select:


			select * from (

		 select A.*,  rowNum from (
		 	select create_user as createUser,tepl_name as teplName,create_time as createTime,dim_json as dimJson,tepl_id as teplId from td_tvcm_template_info

            and create_user=#{createUser}
            and tepl_name=#{teplName}
            and create_time=#{createTime}
            and dim_json=#{dimJson}
            and tepl_id=#{teplId}
            ${sqlWhere}

        order by ${sqlOrderBy}
			) A

		where rowNum <= #{sqlEndIndex}+1 
		) where rowNum >=#{sqlStartIndex}+1

mapper.update:
update td_tvcm_template_info
create_user=null, create_user=#{createUser}, tepl_name=null, tepl_name=#{teplName}, create_time=null, create_time=#{createTime}, dim_json=null, dim_json=#{dimJson}, tepl_id=null, tepl_id=#{teplId},
${sqlWhere}
